import React, { Component } from 'react'
import request from 'superagent'

class CreateScreen extends Component {
  constructor(props) {
    super(props)
    this.state = {
      title: '',
      body: '',
    }
    this.handleBodyInput = this.handleBodyInput.bind(this)
  }
  handleBodyInput(e) {
    this.setState({ body: e.target.value })
  }
  handleTitleInput(e) {
    this.setState({ title: e.target.value })
  }
  handleSubmitMessage() {
    console.log('starting to submit profile')
    if (this.state.isFormFilledProfile) {
      console.log('Profile Form appears filled')
      const data = {
        body: this.state.message,
      }

      request
        .post('/api/v1/posts')
        .send(data)
        .set('Accept', 'application/json')
        .end((err, res) => {
          if (err || !res.ok) {
            console.log('Oh no! err')
          } else {
            console.log('Success')
          }
        })
    }
  }
  render() {
    return (
      <div>
        <div>
          <form onSubmit={this.handleSubmitMessage}>
            <input onChange={this.handleTitleInput} value={this.state.title} />
            <input onChange={this.handleBodyInput} value={this.state.body} />
            <button type='Submit' value='Submit'>
              Submit
            </button>
          </form>
        </div>
      </div>
    )
  }
}

export default CreateScreen
