import React, { Component } from 'react'
import { Container } from 'react-bootstrap'

import axios from 'axios'

class SubmitForm extends Component {
  state = {
    title: '',
    body: '',
  }
  /* This is where the magic happens
   */
  handleSubmit = (event) => {
    event.preventDefault()
    const post = {
      title: this.state.title,
      body: this.state.body,
    }
    axios.post('localhost:5000/api/v1/posts', { post }).then((res) => {
      console.log(res)
      console.log(res.data)
      window.location = '/posts' //This line of code will redirect you once the submission is succeed
    })
  }
  handleChange = (event) => {
    this.setState({ title: event.target.value, body: event.target.value })
  }
  render() {
    return (
      <Container>
        <form onSubmit={this.handleSubmit}>
          <label>
            {' '}
            Title:
            <input type='text' name='title' onChange={this.handleChange} />
          </label>
          <label>
            {' '}
            Body:
            <input type='text' name='body' onChange={this.handleChange} />
          </label>
          <button type='submit'> Add </button>
        </form>
      </Container>
    )
  }
}
export default SubmitForm
